mapper.update:
INSERT INTO files(fnum, gid, fname, fsize) SELECT FILES_SEQ.NEXTVAL AS fnum, t1.* FROM (
SELECT (SELECT MAX(gid) FROM items) AS gid, #{item.fname}, #{item.fsize} FROM DUAL
) t1

mapper.select:
SELECT i.*, f.fnum, f.fname, f.fsize 
FROM items i LEFT OUTER JOIN files f
ON i.gid=f.gid WHERE i.gid=#{gid}

SELECT i.name FROM items i INNER JOIN chatroom cr 
ON i.gid=cr.gid
WHERE cr.roomid =#{roomId}
